CREATE TABLE `NEGATIVE_TRANSACTIONS` ( `ID` int(11) NOT NULL AUTO_INCREMENT, `RECEIPTID` int(11) NOT NULL, `BILLID` int(11) NOT NULL, `PROFILEID` int(11) NOT NULL, `TYPE` enum('RS','DOL') NOT NULL, `AMOUNT` double NOT NULL, `CANCEL_TYPE` varchar(25) NOT NULL, `ENTRY_DT` datetime NOT NULL,PRIMARY KEY (`ID`), KEY `RECEIPTID` (`RECEIPTID`), KEY `BILLID` (`BILLID`), KEY `PROFILEID` (`PROFILEID`), KEY `ENTRY_DT` (`ENTRY_DT`)) ENGINE=InnoDB