SET SQL_MODE="NO_AUTO_VALUE_ON_ZERO";

USE `MIS`;

CREATE TABLE IF NOT EXISTS `MIS_MAINPAGE` (
  `ID` int(11) NOT NULL AUTO_INCREMENT,
  `NAME` varchar(100) COLLATE utf8_bin NOT NULL,
  `MAIN_URL` varchar(250) COLLATE utf8_bin NOT NULL,
  `JUMP_URL` varchar(250) COLLATE utf8_bin DEFAULT NULL,
  `PRIVILEGE` varchar(250) COLLATE utf8_bin NOT NULL,
  `ACTIVE` enum('Y','N') COLLATE utf8_bin NOT NULL DEFAULT 'Y',
  PRIMARY KEY (`ID`),
  KEY `ID` (`ID`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_bin AUTO_INCREMENT=95;

